import { useState } from 'react'
import './styles.css'

function Square({value,onClick}){
    return <button onClick={onClick} className="square">{value}</button>
}

export default function TicTacToe(){

    const[squares,setSquares] = useState(useState(Array(9).fill('')));
    const[isXturn,setIsXturn] = useState(true);

    function getWinner(squares){
        const winningpatters = [
            [0,1,2],
            [3,4,5],
            [6,7,8],
            [2,5,8],
            [0,4,8],
            [2,4,6],
            [0,3,6],
            [1,4,7]
        ];
        for(let i=0;i<winningpatters.length ;i++){
            const [x,y,z] = winningpatters[i];
            if(squares[x] && squares[x] === squares[y] && squares[x]===squares[z]){
                return squares[x];
            }
        }
        return null;
    }

    function handleClick(currSquare) {
        let cpySquares = [...squares];
        if(cpySquares[currSquare] ) return;
        cpySquares[currSquare] = isXturn ? 'X' : 'O';
        setIsXturn(!isXturn);
        setSquares(cpySquares);

        // if (cpySquares[currSquare] !== '') {
        //   cpySquares[currSquare] = isXturn ? 'X' : 'O';
        //   setIsXturn(!isXturn);
        //   setSquares(cpySquares);
        // } else {
        //   alert('kya karhe ho sirji');
        // }
      }

    return(
        <div className="tic-tac-toe-container">
            <div className="row">
                <Square value={squares[0]} onClick={() => handleClick(0)} />
                <Square value={squares[1]} onClick={() => handleClick(1)} />
                <Square value={squares[2]} onClick={() => handleClick(2)} />
            </div>
            <div className="row">
                <Square value={squares[3]} onClick={() => handleClick(3)} />
                <Square value={squares[4]} onClick={() => handleClick(4)} />
                <Square value={squares[5]} onClick={() => handleClick(5)} />
            </div>
            <div className="row">
                <Square value={squares[6]} onClick={() => handleClick(6)} />
                <Square value={squares[7]} onClick={() => handleClick(7)} />
                <Square value={squares[8]} onClick={() => handleClick(8)} />
            </div>
        </div>
    )
}
