import React, { useState } from 'react'
import './style.css'
import data from '../00_practice/data'

const Accordian = () => {

    const[selected,setSelected]=useState(null);
    const[multiEnabled,setMultiEnabled] = useState(false);
    const[mutliples,setMultiples] = useState([]);

    function handleMultiSelected(currId){
        let cpyMultiple = [...mutliples];
        const findindexofId = cpyMultiple.indexOf(currId);
        (findindexofId === -1) ? cpyMultiple.push(currId) : cpyMultiple.splice(findindexofId,1)
        setMultiples(cpyMultiple);
    }

  return (

    <div className='wrapper'>
        <button onClick={()=>setMultiEnabled(true)}> Enable multi select</button>

        <div className='accordian'>
        {
            data ?
            data.map((item)=>
            <div className='abcd'>
                <div className='title' onClick={
                    multiEnabled ? handleMultiSelected(item.id)
                    : ()=>setSelected(item.id)
                }>
                    <h3>{item.question}</h3>
                    <span>+</span>
                </div>
                {(multiEnabled
                ? mutliples.includes(item.id)
                : selected === item.id) && (
                <div className="answer">{item.answer}</div>
              )}
            </div>
            )
            :null
        }
        </div>
    </div>
  )
}

export default Accordian;
