import React, { useEffect, useState } from 'react'
import {BsArrowLeftCircleFill , BsArrowRightCircleFill} from 'react-icons/bs'
import './styles.css'


const ImageSlider = () => {

    const[imageData,setImageData] = useState([]);
    const[loading,setLoading] = useState(false);
    const[errorMsg,setErrorMsg] = useState(null);
    const[currentSlide,setCurrentSlide] = useState(0);

    async function fetchImages(){
        try{
            setLoading(true);
            const response = await fetch('https://picsum.photos/v2/list?page=1&limit=10');
            const data = await response.json();

            if(data && data.length>0){
                setImageData(data);
                setLoading(false);
            }
        }catch(e){
            setErrorMsg(e.message);
            setLoading(false);
        }
    }

    useEffect(()=>{
        fetchImages();
    },[])

    if(loading){
        return <div>Loading...please wait</div>;
    }
    if(errorMsg){
        return <div>Error: {errorMsg}</div>;
    }

    function handlePrevious(){
        setCurrentSlide(currentSlide === 0 ? imageData.length-1 : currentSlide-1);
    }

    function handleNext(){
        setCurrentSlide(currentSlide === imageData.length-1 ? 0 : currentSlide+1);
    }

  return (
    <div className='container'>
        <BsArrowLeftCircleFill className='arrow arrow-left' onClick={handlePrevious}/>
            {
                imageData && imageData.length ?
                imageData.map((val)=>
                <img
                key={val.id}
                alt={val.download_url}
                src={val.download_url}
                className='current-image'
                />
                )
                : null
            }
        <BsArrowRightCircleFill className='arrow arrow-right' onClick={handleNext} />
        <span className='circle-indicators'>
            {
                imageData && imageData.length ?
                imageData.map((_,index)=>
                <button
                key={index}
                className='current-indicator'
                >
                </button>
                )
                :null
            }
        </span>
    </div>
  )
}

export default ImageSlider
