//single selection accordian

import { useState } from "react"
import data from "./data";
import './styles.css'

//multi selection accordian
export default function Accordian(){

    const[selected,setSelected] = useState(null);

    function handleSingleSelection(currId){
        console.log(currId);
        setSelected(currId === selected ?null : currId);        
    }

    return(
        <div className="wrapper">
            <button> Enable multi selection</button>

            <div className="accordian">
                {
                    data && data.length > 0 ? 
                    data.map((item)=> <div className="item">
                        <div className="title" onClick={()=>handleSingleSelection(item.id)}>
                            <h3>{item.question}</h3>
                            <span>+</span>
                        </div>
                        {
                            selected === item.id ?
                            <div className="answer">
                                {item.answer}
                            </div>
                            :null
                        } 
                    </div>)
                    :<div> No data found</div>
                }
            </div>
        </div>
    )
}
