import React, { useState } from 'react'
import './style.css'
import data from '../00_practice/data'

const Accordian = () => {

    const[selected,setSelected]=useState(null);
    const[multiEnabled,setMultiEnabled] = useState(false);
    const[mutliples,setMultiples] = useState([]);

  return (
    <div className='wrapper'>
        <button onClick={()=>setMultiEnabled(true)}> Enable multi select</button>

        <div className='accordian'>
        {
            data ?
            data.map((item)=>
            <div className='abcd'>
                <div className='title' onClick={()=>setSelected(item.id)}>
                    <h3>{item.question}</h3>
                    <span>+</span>
                </div>
                {
                    selected === item.id ? <p>{item.answer}</p> : null
                }
            </div>
            )
            :null
        }
        </div>
    </div>
  )
}

export default Accordian;
