import { useState } from "react";
import data from "./data";
import "./styles.css";

export default function Accordian(){

    const[selected,setSelected] = useState(null);
    const[enableMultiSelection, setEnableMultiSelection] = useState(false);
    const[multiple,setMultiple] = useState([]);

    function handleSingleSelection(currId){
        setSelected(currId===selected ? null : currId)
    }
    function handleMultiSelection(currId){
        let cpyMultiple = [...multiple];
        let findIndexOfCurrentId = multiple.indexOf(currId);
        (findIndexOfCurrentId === -1) ? multiple.push(currId) : multiple.splice(findIndexOfCurrentId,1);

        setMultiple(cpyMultiple);
    }
    return(
        <div className="wrapper">
            <button onClick={() => setEnableMultiSelection(!enableMultiSelection)}>
                {enableMultiSelection ? "Disable Multi Selection" : "Enable Multi Selection"}
            </button>
            <div className="accordian">
                {
                    data.map((item)=>
                    <div className="item" key={item.id} onClick={
                        enableMultiSelection
                            ? ()=>handleMultiSelection(item.id)
                            : ()=>handleSingleSelection(item.id)
                        }>
                        <div className="title" >
                            <h3>{item.question}</h3>
                            <span>+</span>
                        </div>
                        {
                            (enableMultiSelection? multiple.includes(item.id) : selected === item.id) &&
                            (<div className="answer">{item.answer}</div>)
                        }
                        {/* {selected===item.id ? <div className="answer">{item.answer}</div> :null} */}
                    </div>    
                    )
                }
            </div>
        </div>
    )
}
