import { useState } from 'react'
import './styles.css'

function Square({value}){
    return <button className="square">{value}</button>
}

export default function TicTacToe(){

    const[squares,setSquares] = useState(useState(Array(9).fill('')));
    const[isXturn,setIsXturn] = useState(true);

    function handleCLickMethod(currSquare) {
        let cpySquares = [...squares];
        if (cpySquares[currSquare] === '') {
          cpySquares[currSquare] = isXturn ? 'X' : 'O';
          setIsXturn(!isXturn);
          setSquares(cpySquares);
        } else {
          alert('kya karhe ho sirji');
        }
      }

    return(
        <div className="tic-tac-toe-container">
            <div className="row">
                <Square onClick={()=>handleCLickMethod(1)}/>
                <Square onClick={()=>handleCLickMethod(2)}/>
                <Square onClick={()=>handleCLickMethod(3)}/>
            </div>
            <div className="row">
                <Square onClick={()=>handleCLickMethod(4)}/>
                <Square onClick={()=>handleCLickMethod(5)}/>
                <Square onClick={()=>handleCLickMethod(6)}/>
            </div>
            <div className="row">
                <Square onClick={()=>handleCLickMethod(7)}/>
                <Square onClick={()=>handleCLickMethod(8)}/>
                <Square onClick={()=>handleCLickMethod(9)}/>
            </div>
        </div>
    )
}
