import React, { useEffect, useState } from 'react'
import './scroll.css'

const ScrollIndicator = () => {

    const[data,setData] = useState([]);
    const[loading,setLoading] = useState(false);
    const[errorMsg,setErrorMsg] = useState(null);
    const[scrollPercentage,setScrollPercentage] = useState(0);

    async function fetchData() {
        try{
            setLoading(true);
            const response = await fetch('https://dummyjson.com/products?limit=100');
            const data = await response.json();
            if(data && data.products && data.products.length){
                setData(data.products);
                setLoading(false);
            }
        }catch(e){
            setErrorMsg(e.errorMsg);
            setLoading(false);
        }
    }

    useEffect(()=>{
        fetchData();
    },[])

    useEffect(()=>{
        window.addEventListener('scroll',handleScrollPercentage);

        return () =>{
            window.removeEventListener('scroll',()=>{})
        }
    },[])

    if(loading){
        return <div>loading bhai .. please wait</div>
    }
    if(errorMsg){
        return <div>Error: {errorMsg}</div>
    }

  return (
    <div className='scroll-container'>
        <div className='scroll-top-container'>
            <h1>Custom Scroll Indicator</h1>
            <div className='scroll-progress-container-2'>
                <div className='current-progress-bar-2' style={{width: scrollPercentage}}>
                </div>
            </div>
        </div>
        <div className='scroll-data-container'>
            {
                data && data.length ?
                data.map((dataItem)=>
                <div className='product'>
                    <span>{dataItem.title}</span>
                </div>
                )
                :null
            }
        </div>
    </div>
  )
}

export default ScrollIndicator
