//single selection accordian

import { use, useState } from "react"
import data from "./data";
import './styles.css'

//multi selection accordian
export default function Accordian(){

    const[selected,setSelected] = useState(null);
    const[enableMultiSelection, setenableMultiSelection] = useState(false);
    const[multiple,setMultiple] = useState([]);


    function handleSingleSelection(currId){
        console.log(currId);
        setSelected(currId === selected ?null : currId);        
    }

    function handleMultiSelection(currId){
        let cpyMultiple = [...multiple];
        const findIndexOfCurrentId = cpyMultiple.indexOf(currId);
        console.log(findIndexOfCurrentId);
    }

    return(
        <div className="wrapper">
            <button onClick={()=>setenableMultiSelection(!enableMultiSelection)}> Enable multi selection</button>

            <div className="accordian">
                {
                    data && data.length > 0 ? 
                    data.map((item)=> <div className="item">
                        <div className="title" 
                            onClick={ enableMultiSelection
                            ? ()=>handleMultiSelection(item.id) 
                            : ()=>handleSingleSelection(item.id)
                            }>
                            <h3>{item.question}</h3>
                            <span>+</span>
                        </div>
                        {
                            selected === item.id ?
                            <div className="answer">
                                {item.answer}
                            </div>
                            :null
                        } 
                    </div>)
                    :<div> No data found</div>
                }
            </div>
        </div>
    )
}
