import { useEffect, useState } from "react"
import './styles.css'

function Squares({value, onClick}){
    return <button className="square" onClick={onClick}>{value}</button>
}

export default function TicTacToe(){
    const[squares,setSquares] = useState(Array(9).fill(''));
    const[isXturn,setIsXturn] = useState(true);
    const[status,setStatus] = useState('');

    function getWinner(){
        const winningPatterns=[
            [0,1,2],
            [3,4,5],
            [6,7,8],
            [0,3,6],
            [1,4,7],
            [2,5,8],
            [0,4,8],
            [2,4,6],
        ];

        for(let i=0;i<winningPatterns.length ; i++){
            const[x,y,z] = winningPatterns[i];
            if(squares[x] && squares[x]===squares[y] && squares[x]===squares[z]) return squares[x];
        }
        return null;
    }

    function handleClick(currSqId){
        let cpySquares= [...squares];
        if(cpySquares[currSqId]) return;
        cpySquares[currSqId] = isXturn?'X':'O';
        setSquares(cpySquares);
        setIsXturn(!isXturn);
    }

    useEffect(()=>{
        if(!getWinner(squares) && squares.every(item=>item!==null)) setStatus('this is a draw. please restart')
            else if(getWinner(squares)) setStatus(`Winner is ${getWinner(squares)}`)
        else setStatus(`Next turn: ${isXturn?'X':'O'}`);
    },[])

    return(
        <div className="tic-tac-toe-container-2">
            <div>
                <Squares value={squares[0]} onClick={()=>handleClick(0)} />
                <Squares value={squares[1]} onClick={()=>handleClick(1)} />
                <Squares value={squares[2]} onClick={()=>handleClick(2)} />
            </div>
            <div>
                <Squares value={squares[3]} onClick={()=>handleClick(3)} />
                <Squares value={squares[4]} onClick={()=>handleClick(4)} />
                <Squares value={squares[5]} onClick={()=>handleClick(5)} />
            </div>
            <div>
                <Squares value={squares[6]} onClick={()=>handleClick(6)} />
                <Squares value={squares[7]} onClick={()=>handleClick(7)} />
                <Squares value={squares[8]} onClick={()=>handleClick(8)} />
            </div>
            <h1 style={{color:'darkgray'
            }}>{status}</h1>
        </div>
    )
}
