import React, { useState } from 'react'
import './style.css'
import data from '../00_practice/data'

const Accordian = () => {
    const[selected,setSelected] = useState(null);
    const[enableMultiSelection, setEnableMultiSelection] = useState(false);
    const[multiple,setMultiple] = useState([]);

    function handleSingleSelect(currId){
        setSelected(currId === selected ? null : currId);
    }

    function handleMultiSelect(currId){
        let cpyMultiple = [...multiple];
        const findindexofId = cpyMultiple.indexOf(currId);
        (findindexofId === -1) ? cpyMultiple.push(currId) : cpyMultiple.splice(findindexofId,1);
        setMultiple(cpyMultiple);
    }

  return (
    <div className='wrapper'>
      <button onClick={()=>setEnableMultiSelection(!enableMultiSelection)
      }>
        {enableMultiSelection ? "Disable Multi Selection" : "Enable Multi Selection" }
      </button>
      <div className='accordian'>
        {
            data && data.length>0 ?
            (
                data.map((item)=>
                <div className='item' key={item.id}>
                    <div className='title'
                    onClick={
                        enableMultiSelection ? 
                        handleMultiSelect(item.id)
                        :handleSingleSelect(item.id)
                    }
                    >
                        <h3>{item.question}</h3>
                        <span>+</span>
                    </div>
                    {
                        (enableMultiSelection ? multiple.includes(item.id): selected === item.id)&&
                        <div>{item.answer}</div>
                    }
                </div>
                )
            )
            : (
                <div>No data found </div>
            )
        }
      </div>
    </div>
  )
}

export default Accordian
