export default function User({user}){
    const {avatar_url, followers, following, created_at, name,  login, public_repos} = user;

    const createdDate = new Date(created_at);

    return(
        <div className="user">
            <div className="name-container">
                <img src={avatar_url} alt="user" className="avatar" />
            </div>
            <div>
                <a href={`https://github.com/${login}`}> {name|| login}</a>
                <p>User joined on {`${createdDate.getDate()} ${createdDate.toLocaleString('en-us',{month:'short'})} 
                ${createdDate.getFullYear()}`}</p>
            </div>
        </div>
    )
}
