*/

import { useEffect, useState } from 'react'
import './styles.css'
import { BsCake } from 'react-icons/bs';

function Sqaure({value, onClick}){
    return <button className="square" onClick={onClick}>{value}</button>
}

export default function TicTacToe(){

    const[squares,setSquares] = useState(Array(9).fill(''));
    const[isXturn,setIsXturn] = useState(true);
    const[status,setStatus] = useState('');

    function getWinner(squares){
        const winningPatterns = [
            [0, 1, 2],
            [3, 4, 5],
            [6, 7, 8],
            [2, 5, 8],
            [0, 4, 8],
            [2, 4, 6],
            [0, 3, 6],
            [1, 4, 7],
        ];
        for (let i = 0; i < winningPatterns.length; i++) {
            const [x, y, z] = winningPatterns[i];

            if (
              squares[x] &&
              squares[x] === squares[y] &&
              squares[x] === squares[z]
            ) {
              return squares[x];
            }
          }

          return null;
    }

    function handleClick(currSquareId){
        let cpySquares = [...squares];
        if(getWinner(cpySquares) || cpySquares[currSquareId]) return;
        cpySquares[currSquareId] = isXturn ? 'X' : 'O';
        setIsXturn(!isXturn);
        setSquares(cpySquares);
    }

    useEffect(()=>{
        if(!getWinner(squares) && squares.every(item => item!=='')) setStatus('This is a draw. restart');
        else if(getWinner(squares)){
            setStatus(`Winner is ${getWinner(squares)}`);
        } else{
            setStatus(`Next player is ${isXturn ? 'X' : 'O'}`);
        }
    },[squares,isXturn]);

    return(
        <div className="tic-tac-toe-container">
            <div className="row">
                <Sqaure value={squares[0]} onClick={()=>handleClick(0)} />
                <Sqaure value={squares[1]} onClick={()=>handleClick(1)} />
                <Sqaure value={squares[2]} onClick={()=>handleClick(2)} />
            </div>
            <div className="row">
                <Sqaure value={squares[3]} onClick={()=>handleClick(3)} />
                <Sqaure value={squares[4]} onClick={()=>handleClick(4)} />
                <Sqaure value={squares[5]} onClick={()=>handleClick(5)} />
            </div>
            <div className="row">
                <Sqaure value={squares[6]} onClick={()=>handleClick(6)} />
                <Sqaure value={squares[7]} onClick={()=>handleClick(7)} />
                <Sqaure value={squares[8]} onClick={()=>handleClick(8)} />
            </div>
            <h1 style={{ color:'black'}}>{status}</h1>
        </div>
    )
}
