export default function Modal({id, header, body, footer}){
    return(
        <div className="modal-container" key={id}>
            <div className="modal-header">
                {header ? header : <h1>Header</h1>}
                <div>&times;</div>
            </div>
            <div className="modal-body">
                {body ? body : <div>This is the content in Body.</div>}
            </div>
            <div className="modal-footer">
                {footer ? footer : <h1>Footer</h1>}
            </div>
        </div>
    )
}
